current.custom_css:
.sf-topbar {
  border-color: #eee;
}
.sf__index-icon-box .section-my {
  padding-top: 45px;
  padding-bottom: 45px;
  margin-bottom: 0;
  margin-top: 0;
}
.sf__index-icon-box {
  border-top: 1px solid #eee;
}
.sf__font-normal {
  font-weight: 400;
}
.sf__index-collection-list .collection-card__info h3 a {
  font-weight: 400;
  font-size: 24px;
  line-height: 34px;
  margin-bottom: 4px;
}
.sf__index-collection-list .collection-card__info p {
  color: #666;
}
.sf__index-icon-box .sf-icon-box__text h3 {
  line-height: 26px;
  font-size: 20px;
  font-weight: 500;
}
@media (min-width: 1536px) {
  .sf__index-slideshow .slide__block-title {
    font-size: 100px;
    line-height: 95px;
  }
}
@media (max-width: 576px) {
  .sf__index-slideshow a.sf__mobile-button,
  .sf__index-product-tab .sf__btn-primary,
  .sf__index-product-tab .sf__btn-secondary {
    width: 100%;
  }
  .sf__index-collection-list .section__heading {
    text-align: center;
  }
  .sf__index-collection-list .collection-card__info h3 a {
    font-weight: 500;
  }
  .sf__index-collection-list .collection-card__info p {
    font-size: 14px;
    line-height: 20px;
  }
  .sf__index-icon-box .sf-icon-box__text h3 {
    font-size: 18px;
    margin-bottom: 2px;
  }
}
