current.custom_css:
.sf__index-icon-box-1 .sf-icon-box__text .sf__btn.sf__btn-link {
  padding: 0;
  font-weight: 400;
}
.sf__index-icon-box-1 .sf-icon-box__text .sf__btn.sf__btn-link::before {
  background-color: rgba(0, 0, 0, 0.25);
  height: 1px;
}
.sf__index-icon-box-1 .sf-icon-box__text .sf__btn.sf__btn-link::after {
  height: 1px;
}
.sf__index-img-with-text .section-my {
  margin-top: -30px;
}

.sf__pcard-vendor {
  font-weight: 500;
}
.sf__footer-form-newsletter input.form-control {
  background: #fff;
}
.sf__index-img-with-text .sf-custom__grid {
  margin-top: 0;
}
.sf__index-icon-box-2 .sf-icon-box__text h3 {
  line-height: 26px;
  font-weight: 500;
  font-size: 20px;
}
@media (min-width: 1536px) {
  .sf__index-img-with-text .content__box h3 {
    font-size: 100px;
    line-height: 100px;
    margin-bottom: 30px;
  }
  .sf__index-img-with-text .content__box p {
    line-height: 32px;
  }
}
@media (max-width: 1279px) {
  .sf__index-img-with-text .section-my {
    margin-top: 0;
  }
}

@media (max-width: 767px) {
  .sf__index-img-with-text .content__box h3 {
    font-size: 36px;
    line-height: 52px;
  }
  .sf__index-img-with-text .content__box .content__subheading {
    margin-bottom: 5px;
  }
  .sf__index-img-with-text .content__box div {
    margin-bottom: 15px;
  }
  .sf__index-img-with-text .section-my {
    padding-left: 0;
    padding-right: 0;
  }
  .sf__index-img-with-text .content__box {
    text-align: center;
    margin-top: 25px;
    padding-left: 25px;
    padding-right: 25px;
  }
}
@media (min-width: 768px) {
  .sf__index-icon-box-2 .section-my {
    padding-top: 45px;
    padding-bottom: 45px;
    margin-bottom: 0;
    margin-top: 0;
  }
  .sf__index-icon-box-2 {
    border-top: 1px solid #eee;
  }
}
@media (min-width: 1024px) {
  .sf__index-collection-list .collection-card__info h3 a {
    font-weight: 400;
    font-size: 24px;
    line-height: 34px;
    margin-bottom: 2px;
  }
}
@media (max-width: 576px) {
  .sf__index-featured-collection .sf__featured-collection-button a.sf__btn {
    width: 100%;
  }
  .sf__index-icon-box-2 .sf-icon-box__text h3 {
    font-size: 18px;
    margin-bottom: 2px;
  }
}
