current.custom_css:
.sf__index-img-with-text .section-my {
  margin-top: 0;
}

.sf__index-product-tab .sf-tab-header {
  font-family: "SF-Heading-font";
  font-size: 40px;
  line-height: 1.3;
}
.sf__index-product-tab .sf-tab-header.active:after {
  width: 0;
}
.sf__index-icon-box .sf-icon-box__text h3 {
  font-family: "SF-Body-font";
}
.sf__index-custom-content .section-my {
  margin-top: 0;
  margin-bottom: 40px;
}
.sf__index-icon-box .section-my {
  margin-top: 0;
}
.sf__index-instagram .section-my {
  margin-bottom: 0;
}
.sf__footer-block-newsletter .sf__footer-block-title {
  font-family: "SF-Body-font";
  font-size: 36px;
  line-height: 1.4;
  margin-bottom: 12px;
  font-weight: 400;
}
.sf__footer-block-title {
  font-size: 24px;
  line-height: 1.33;
}

.sf__pcard-name {
  font-weight: 400;
}
.sf__pcard-price .prod__price {
  font-weight: 600;
}
.sf__pcard-price.price--on-sale .prod__price {
  color: #b22234;
}
.section__subheading {
  letter-spacing: 0.2em;
  font-weight: 400;
}

.sf__index-img-with-text .sf__btn.sf__btn-link,
.sf__index-icon-box .sf__btn.sf__btn-link {
  padding-left: 10px !important;
  padding-right: 10px !important;
  letter-spacing: 0.1em;
}
.section__header .section__heading,
.sf__index-img-with-text .content__box .content__heading {
  line-height: 1.3;
}
.sf__btn.sf__btn-link::after,
.sf__btn.sf__btn-link::before {
  height: 1px !important;
}

.sf-footer form.sf__footer-form-newsletter .sf__newletter__form-input {
  border-color: #d4d4d4;
}
@media screen and (min-width: 768px) {
  .section-my {
    margin-top: 100px;
    margin-bottom: 100px;
  }
  .section-py {
    padding-top: 100px;
    padding-bottom: 100px;
  }
}
@media screen and (min-width: 1280px) {
  .sf__index-collection-list .collection-card--hover-scaling-up:hover .collection-card__image {
    transform: scale(1.2) translateZ(0);
  }
  .sf__index-collection-list .collection-card__image {
    width: 150px;
    height: 150px;
    margin: 0 auto;
  }
}

@media (max-width: 767px) {
  .sf__index-img-with-text [class*="content__box-"] {
    padding-left: 30px;
    padding-right: 30px;
  }
  .sf__index-img-with-text .content__heading {
    font-size: 30px;
  }
  .section__subheading {
    font-size: 12px;
    margin-bottom: 10px;
  }
  .sf__index-icon-box .sf__btn.sf__btn-link {
    margin-top: 0;
  }
  .sf__index-product-tab .sf__custom-select {
    font-family: "SF-Heading-font";
  }
  .sf__index-collection-list .sf__ms-wrapper:not(.swiper-container-initialized) .sf-column {
    width: 42vw !important;
  }
  .sf__footer-block-title {
    font-size: 18px;
  }
}
