current.custom_css:
.sf-nav li.sf-menu-item .sf__sub-menu-link1 {
  font-weight: 700;
}

.sf__index-slideshow .slide__block-desktop .slide__block-title {
  line-height: 1.175;
  font-size: 80px;
  margin-bottom: 18px;
}

.sf__index-slideshow .slide__block-description p {
  color: var(--btn-color);
  font-weight: 700;
  font-size: 20px;
  line-height: 23px;
}
.sf__index-custom-content #sf-custom__block--302f4255-280e-48a3-9f00-b9e6fd50230a .image__card-heading {
  font-size: 24px;
  line-height: 1.3;
  letter-spacing: 1px;
}
.sf__index-custom-content #sf-custom__block--190cefb9-e7ca-4316-8e55-b3b82656701b
  .image__card-heading {
  line-height: 1.167;
  letter-spacing: 3px;
}
.sf__index-custom-content-1 .sf__scrolling-text[data-style="text-outline"][data-outline-thickness="thin"] em {
  -webkit-text-stroke-width: 0.5px;
  -webkit-text-fill-color: rgba(0, 0, 0, 0);
  font-style: italic;
}
.sf__index-custom-content-1 .sf__scrolling-text[data-style="text-outline"] em {
  -webkit-text-stroke-color: #dfdfdf;
}
.sf__index-custom-content-1 .sf__scrolling-text .sf__scrolling-text-inner span.custom {
  padding: 0 25px;
}
.sf__index-custom-content-1 .sf__scrolling-text.font_size_11vh * {
  font-size: 110px;
  letter-spacing: 10px;
}
.sf__index-custom-content-1 .section-my {
  margin-bottom: 20px;
}
.sf__newsletter-section .newsletter__form-heading h3 {
  line-height: 1.15;
  letter-spacing: 2px;
}

.sf-footer__block h3.sf__accordion-button {
  font-size: 14px;
  font-weight: 700;
}
.sf__index-custom-content .image-card {
  height: 100%;
}
.section__heading {
  letter-spacing: 2px;
}

.sf__pcard-content a {
  font-weight: 700 !important;
  text-transform: uppercase;
}
.sf-footer__block-inner h3 {
  font-weight: 700;
}

.collection-card h3 a {
  font-weight: 700 !important;
  text-transform: uppercase;
}
.sf__newsletter-section .section-my {
  margin-top: -80px;
  position: relative;
}

@media (max-width: 767px) {
  .sf__index-slideshow .sf__mobile-button {
    width: 100%;
  }
  .sf__index-custom-content .image-card__content {
    position: relative;
    padding-left: 0;
    padding-right: 0;
    padding-bottom: 0;
  }
  .sf__index-custom-content .image-card__content .image-card__content-inner {
    text-align: center;
    width: 100%;
  }
  .sf__index-custom-content .image__card-heading br {
    display: none;
  }
  .sf__index-custom-content #sf-custom__block--302f4255-280e-48a3-9f00-b9e6fd50230a .image__card-heading {
    margin-bottom: 5px;
  }
  p.section__subheading {
    margin-bottom: 5px;
  }
}
@media (min-width: 768px) {
  .sf__footer-block-custom_text .sf__footer-block-title {
    display: none;
  }
}
@media (min-width: 1024px) {
  .sf__footer-bottom-inner {
    border-top: 1px solid #d8d8d8;
  }
}
@media (min-width: 1280px) {
  .sf__index-custom-content #sf-custom__block--190cefb9-e7ca-4316-8e55-b3b82656701b .image__card-heading {
    font-size: 60px;
    margin-bottom: 42px;
  }
  .sf__newsletter-section .newsletter__form-heading h3 {
    font-size: 40px;
  }
}
