current.custom_css:
.hero-heading {
  font-size: 100px;
  font-weight: 800;
  letter-spacing: 12.5px;
  text-transform: uppercase;
}
.sf-slider__arrows button {
  border-color: white;
}
.sf__slide-footer {
  font-weight: 600;
}
.sf-slider__arrows button svg path {
  fill: #fff;
}

.sf-custom .sf__pcard-vendor {
  display: none;
}
.sf-custom .sf__pcard-content {
  margin-top: 20px;
}
.sf__newsletter-section .section-my {
  margin-bottom: 0;
}
.sf__index-collection-list sup {
  top: -1em;
  right: -0.2em;
}

.sf-testimonials .section__heading {
  font-size: 20px;
}
.sf__footer-block-our_store .social-media-links {
  margin: 0px -28px;
  margin-top: 25px;
}
.sf__footer-block-our_store .social-media-links a {
  padding: 0 20px;
}
.sf__index-icon-box-1 {
  border-top: 1px solid #eeeeee;
}
.sf__index-icon-box-1 .section-py {
  padding-top: 40px;
  padding-bottom: 40px;
}
.sf__index-icon-box-1 .sf-icon-box__text h3 {
  font-weight: 500;
  font-size: 20px;
}
.sf__index-icon-box-1 .sf-icon-box__text .rte {
  color: #777777;
}
.sf__index-collection-list .collection-card__info h3:hover {
  color: #666666;
}
@media (min-width: 768px) {
  .sf__index-collection-list .section-my {
    margin-top: 30px;
  }
}

@media screen and (max-width: 767px) {
  .sf__index-icon-box-1 {
    border-top: 0;
  }
  .sf__index-icon-box-1 .section-py {
    padding-top: 0;
  }
  .sf__index-collection-list .section__header {
    text-align: center;
  }
  .sf__index-collection-list .section__header .section__description {
    display: none;
  }
  .sf__index-slideshow .sf__slide-content--mobile h2 {
    font-weight: 800;
    font-size: 36px;
    line-height: 52px;
    letter-spacing: 5px;
    text-transform: uppercase;
  }
}

@media (max-width: 576px) {
  .sf__index-slideshow .sf__btn-primary,
  .sf__index-product-tab .sf__btn-primary {
    width: 100%;
  }
}
