current.custom_css:
.sf-header__desktop {
  min-height: 91px;
  display: flex;
  align-items: center;
}
.sf__footer-middle {
  padding-top: 40px;
  padding-bottom: 40px;
}
.sf__index-instagram .sf-instagram__wrapper {
  margin: 0 20px;
}
.sf__index-product-slider .featured-slider__products {
  max-width: 300px;
}
.sf__heading-color {
  color: #322b81;
}
.sf__font-size-40 {
  font-size: 40px;
  line-height: 56px;
}
.sf__font-size-24 {
    font-size: 24px;
    line-height: 1.4;
}
.sf__font-bold {
  font-weight: 700;
}
.sf-footer .sf__footer-form-newsletter {
  max-width: 80%;
}
.sf__footer-block-newsletter .sf__footer-block-title {
  margin-bottom: 4px;
}
.sf__footer-block-newsletter .sf__newsletter-block-desc {
  padding: 0;
  margin-bottom: 18px;
  font-weight: 600;
}
.sf__footer-block-menu .sf__footer-block-title {
  font-size: 24px;
  font-weight: 600;
}
.sf__index-instagram .section-my {
  margin-bottom: 0;
}
.sf__text-style1 {
  position: relative;
  z-index: 1;
}
.sf__text-style1::after {
  position: absolute;
  content: "";
  left: 50%;
  bottom: 5px;
  width: calc(100% + 4px);
  height: 8px;
  background: #fbf19b;
  border-radius: 10px;
  z-index: -1;
  transform: translateX(-50%);
}
.sf__font-size-20 {
  font-size: 20px;
  line-height: 32px;
}
.sf__font-size-18 {
  font-size: 18px;
  line-height: 30px;
}
.sf__font-weight-semibold {
  font-weight: 600;
}
.sf__index-icon-box .section-py {
  padding-top: 0;
}
.sf__index-testimonial .review-block .rte {
  margin-bottom: 40px;
}
.sf__index-testimonial .review-block .rte p {
  font-size: 34px;
  font-weight: 600;
  line-height: 48px;
}
.sf-search__form {
  border-radius: 10px !important;
  border-width: 2px !important;
}
.sf__btn {
  font-weight: 700;
  border-radius: 10px;
}
.sf__btn.sf__btn-primary,
.sf__btn.sf__btn-white:hover {
  background: #fdf397 !important;
  border-color: #fdf397 !important;
  color: #000 !important;
}
.sf__index-custom-content-2 {
  text-align: center;
}
.slide__block-subtitle {
  font-weight: 600;
}
.sf__pcard .sf__pcard-name {
  color: #000;
  font-weight: 600;
}
.sf__pcard .prod__price {
  font-weight: 700;
}
.sf__index-blog-posts .section-my {
  margin-bottom: 20px;
}
.sf__index-custom-content-2 .section-my {
  margin-top: 0;
}
.custom__html {
  padding: 45px 60px;
  background: #e6f0f2;
  border-radius: 15px;
  text-align: left;
}
.custom__html .block__text {
  margin-right: 90px;
}
@media (min-width: 768px) {
  .section-my {
    margin-top: 100px;
    margin-bottom: 100px;
  }
  .section-py {
    padding-top: 100px;
    padding-bottom: 100px;
  }
  .home-banner__slideshow .swiper-pagination-bullet:after {
    background-color: #fff;
  }
  .home-banner__slideshow .swiper-pagination-bullet-active {
    border-color: #fff;
  }
}
@media (max-width: 767px) {
  .hide_br_mb br {
    display: none;
  }
  .custom__html {
    padding: 30px 20px;
    text-align: center;
  }
  .custom__html .block__text {
    margin-right: 0;
    margin-bottom: 30px;
  }
  .sf__index-testimonial .review-block .rte p {
    font-size: 24px;
    line-height: 34px;
  }
}
.sf-testimonials.testimonials-4 .sf-slider__controls-button {
  margin-top: 0;
}

.sf-header__desktop .sf-menu__banner .sf__btn::before {
  color: #322b81;
}
.sf-header__desktop .sf-menu__banner .sf__btn::after {
  color: #322b81;
}
.sf-header__desktop .sf-menu__banner .sf__btn {
  color: #322b81;
}
.sf-header__desktop
  .sf-menu__banner
  .sf-menu__banner-inner
  div.flex
  a.text-black {
  color: #322b81;
}

.sf-header__desktop .sf-search-form span {
  order: 2;
}
.sf-header__desktop .sf-search-form button {
  order: 1;
  padding-right: 0;
}
.sf-header__desktop .sf-search-form {
  min-width: 290px;
}

.sf__index-icon-box .sf-image {
  transition: all 0.6s;
}
.sf__index-icon-box .sf-image:hover {
  transform: scale(1.06);
}
.sf__index-featured-collection
  .section__header
  .section__subheading {
  letter-spacing: 0.1rem;
  font-weight: 600;
  font-size: 16px;
  line-height: 22px;
}
.sf__index-featured-collection .section__header .section__heading {
  font-weight: bold;
  font-size: 48px;
  line-height: 56px;
}
.sf__index-featured-collection
  .sf__pcard-action
  .sf__tooltip-item.sf__tooltip-style-1:hover {
  color: #000000;
}
@media (min-width: 1280px) {
  .featured-slider .featured-slider__container .xl\:py-12 {
    padding-top: 2rem;
    padding-bottom: 2rem;
  }
  .sf__hover-scale-down:hover .sf-image--loaded {
      transform: none;
  }
}
@media (max-width: 1024px) {
  .sf-header__desktop {
    display: none;
  }
}
.featured-slider .sf__pcard-content .sf__pcard-name {
  display: none;
}
.featured-slider .sf__pcard-content .sf__pcard-price {
  display: none;
}
.featured-slider .sf__pcard-content .sf__pcard-options-color {
  justify-content: center;
}
.featured-slider .sf__pcard-action .sf__tooltip-item.sf__btn-icon:hover {
  color: #000000;
}
@media(max-width: 767px) {
    [data-section-type="slider"] .sf__mobile-button{
        width: 100%;
    }
    [data-section-type="featured-slider"] .featured-slider__container {
        background: #FFF;
        padding-top: 15px;
    }
    .sf-footer .sf__footer-form-newsletter {
        max-width: 100%;
    }
    .sf-footer .social-media-links a {
        color: var(--color-footer-text);
    }
    .sf-footer .sf__footer-middle {
        padding-bottom: 0;
    }
    [data-section-type="instagram"] .section__description {
        margin-top: 0;
    }
}
[data-section-type="featured-slider"] .featured-slider__product-content h3 {
    color: #000000;
}
