current.custom_css:
.sf-tab-header {
  font-size: 18px;
}
.sf__index-product-tab .section__header {
  margin-bottom: 40px;
}
.sf-footer .form-control.form__control-icon {
  background: #ffffff;
}
.sf__index-icon-box {
  border-top: 1px solid #eeeeee;
}
.sf__index-icon-box .section-py {
  padding-top: 40px;
  padding-bottom: 40px;
}
.sf__index-icon-box .sf-icon-box__text h3 {
  font-weight: 500;
  font-size: 20px;
}
.sf__index-icon-box .sf-icon-box__text .rte {
  color: #777777;
}

.sf__index-product-tab .product-tabs__header .sf-tab-header {
  font-weight: 500;
  line-height: 26px;
}
.sf__index-product-tab .product-tabs__header .sf-tab-header:after {
  bottom: -4px;
}
.sf__index-custom-content .image-card__content-inner {
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  height: 100%;
  justify-content: space-between;
}
.sf__index-custom-content .image-card__content .sf__btn.sf__btn-link::before {
  background-color: rgba(0, 0, 0, 0.25);
  height: 1px;
}
.sf__index-custom-content .image-card__content .sf__btn.sf__btn-link::after {
  height: 1px;
}
.sf__index-custom-content .image-card__content-inner .image__card-subheading,
.sf__index-product-tab .sf__custom-select,
.sf__index-img-with-slider .banner-w-slider__card-title,
.sf__index-img-with-slider .sf__slide-content .sf__content-description {
  font-weight: 500;
}

@media (min-width: 768px) {
  .sf__index-img-with-slider .section-my {
    margin-top: 0;
  }
}
@media (min-width: 1280px) {
  .sf__index-testimonial .sf-testimonials-description {
    font-size: 36px;
    line-height: 52px;
  }
.sf__index-img-with-slider .sf__slide-content .sf__content-description {
    font-size: 20px;
  }
}
@media (min-width: 1536px) {
.sf__index-img-with-slider .sf__slide-content .sf__content-heading {
    font-size: 70px;
    line-height: 90px;
  }
}
@media (max-width: 767px) {
  .sf__index-icon-box {
    border-top: 0;
  }
  .sf__index-icon-box .section-py {
    padding-top: 0;
  }
}
@media (max-width: 576px) {
  .sf__index-icon-box .sf-icon-box__text h3 {
    font-size: 18px;
  }
  .sf__index-product-tab .section__header {
    margin-bottom: 30px;
  }
  .sf__index-slideshow a.sf__mobile-button,
  .sf__index-product-tab .product-tabs__content a.sf__btn {
    width: 100%;
  }
}
@media (max-width: 1920px) {
  .sf__index-testimonial .sf-testimonials-description br {
    display: none;
  }
}

@media (min-width: 768px) {
  .home-banner__slideshow .sf__slider--large {
    height: 85vh;
  }
}
