current.custom_css:
.sf__placeholder-svg {
  border: none;
}
.sf-nav li a.sf__parent-item {
  font-size: 14px;
}
.sf__index-custom-content .prod__option div {
  width: 100%;
}
.sf__index-custom-content .sf__custom-select.sf__select-bordered .select-selected {
  border-radius: 0;
}
.section__heading {
  letter-spacing: 1.5px;
}
.sf__index-instagram .section-my {
  margin-bottom: 0;
}
.sf__pcard-content a {
  text-transform: uppercase;
  letter-spacing: 1px;
  font-size: 14px;
}
.sf__index-img-with-text .sf-hero__subtitle {
  font-size: 16px;
  letter-spacing: 1.5px;
}
.sf__index-img-with-text .sf-hero__title {
  letter-spacing: 6px;

  font-family: "Futura";
}
.sf-header__desktop .sf-nav li a.sf__parent-item {
  margin: 0 0.5rem;
}
.content__subheading,
.sf-hero__subtitle {
  font-size: 16px;
  letter-spacing: 1.5px;
}
.sf__index-img-with-text-2 .sf-hero__title {
  letter-spacing: 4px;
}
.sf-footer .sf__footer-block-our_store .social-media-links {
  margin: 40px -25px 0;
}
.sf-footer .sf__footer-block-our_store .social-media-links a {
  padding: 0 15px;
}
.sf-footer .sf__footer-block-our_store .social-media-links svg path {
  color: #000;
}
.sf__footer-form-newsletter .sf__newletter__form-input {
  border: 1px solid #000;
  border-radius: 0;
}
.sf__index-img-with-text-3 .content__subheading {
  font-size: 12px;
  line-height: 16px;
}
#sf-custom__block--6dfc6c6b-e575-4b68-8e32-4214bcf8d7f7 .prod__title {
  text-transform: uppercase;
  font-size: 30px;
  line-height: 50px;
  letter-spacing: 1.5px;
}
#sf-custom__block--6dfc6c6b-e575-4b68-8e32-4214bcf8d7f7 .prod__vendor {
  font-size: 12px;
  text-transform: uppercase;
  letter-spacing: 0.5px;
  color: #000;
}
#sf-custom__block--6dfc6c6b-e575-4b68-8e32-4214bcf8d7f7 .prod__option-label {
  display: none;
}
.sf__index-img-with-text-2 .sf-hero__title {
  font-family: "Futura";
}
.sf__index-custom-content #sf-custom__block--6dfc6c6b-e575-4b68-8e32-4214bcf8d7f7 {
  width: 40%;
  max-width: 550px;
}

.sf__index-custom-content .sf-custom__grid {
  justify-content: center;
}

.sf__index-custom-content .product-options {
  margin-bottom: 20px;
}
.sf__index-custom-content .product-options select {
  border-radius: 0;
}
@media (min-width: 1024px) {
  .sf__index-img-with-text-3 .content__box h3 {
    font-size: 30px;
    line-height: 50px;
    letter-spacing: 1.5px;
  }
  .sf__index-img-with-text-2 .sf-hero__title {
    font-size: 70px;
  }
  .sf__index-img-with-text .sf-hero__title {
    margin-bottom: 40px;
  }
}
@media (min-width: 1536px) {
  .sf__index-img-with-text .sf-hero__title {
    font-size: 70px;
    line-height: 1.17;
  }
}
@media (min-width: 1661px) {
  .sf__index-img-with-text .sf-hero__title {
    font-size: 100px;
    line-height: 1.17;
  }
}
@media (max-width: 767px) {
  #sf-custom__block--6dfc6c6b-e575-4b68-8e32-4214bcf8d7f7 .prod__title {
    font-size: 24px;
  }
  .sf__index-custom-content #sf-custom__block--6dfc6c6b-e575-4b68-8e32-4214bcf8d7f7 {
    width: 100%;
  }
  .sf__index-custom-content .sf-custom__grid {
    flex-direction: column-reverse;
  }
  .sf__index-img-with-text .sf-hero__inner,
  .sf__index-img-with-text-2 .sf-hero__innner {
    justify-content: center;
  }
  .sf__index-img-with-text .sf-hero__inner .sf-hero__content,
  .sf__index-img-with-text-2 .sf-hero__inner .sf-hero__content {
    text-align: center;
  }
  .sf__index-img-with-text .sf-hero__inner .sf-hero__content .sf-hero__subtitle,
  .sf__index-img-with-text-2 .sf-hero__inner .sf-hero__content .sf-hero__subtitle {
    font-size: 14px;
  }
  .sf__index-img-with-text .sf-hero__inner .sf-hero__content .sf-hero__title,
  .sf__index-img-with-text-2 .sf-hero__inner .sf-hero__content .sf-hero__title {
    font-size: 36px;
    line-height: 42px;
  }
  .sf__index-img-with-text-3 .content__box h3 {
    font-size: 24px;
    line-height: 42px;
  }
  .sf__index-img-with-text .sf-hero__inner .sf-hero__content,
  .sf__index-img-with-text-2 .sf-hero__inner .sf-hero__content,
  .sf__index-img-with-text .sf-hero__inner .sf-hero__content .sf-hero__btn,
  .sf__index-img-with-text-2 .sf-hero__inner .sf-hero__content .sf-hero__btn,
  .sf__index-img-with-text .sf-hero__inner .sf-hero__content .sf-hero__btn a.sf__btn,
  .sf__index-img-with-text-2 .sf-hero__inner .sf-hero__content .sf-hero__btn a.sf__btn,
  .sf__index-img-with-text-3 .content__box a.sf__btn,
  .sf__index-featured-collection .sf__featured-collection-button a.sf__btn {
    width: 100%;
  }
}
