current.custom_css:
.sf__index-slideshow .slide__block-description {
  text-transform: uppercase;
  letter-spacing: 0.1em;
  line-height: 21px;
  font-size: 14px;
  margin-bottom: 36px;
  color: var(--color-text-body);
}
.sf__index-slideshow .slide__block-title {
  line-height: 1.167;
  margin-bottom: 12px;
}
.sf__custom-html-block1 .m-scroll {
  height: 56px;
}
.sf__index-press .rte p {
  font-size: 24px;
  line-height: 36px;
}

.sf__footer-block-newsletter .sf__footer-block-title {
  font-size: 40px;
  line-height: 1.3;
  margin-bottom: 12px;
}
.sf__footer-form-newsletter .form-control {
  border-color: #000;
  border-radius: 0;
}
.sf-header .sf-search__form:not(.mobile).icon-left {
  border-color: #fff;
}
.sf__footer-block-title {
  font-size: 20px;
  line-height: 32px;
}
.sf__pcard-name {
  text-transform: uppercase;
}
.sf__pcard-price .prod__price {
  font-weight: 600;
}
.sf__pcard-price.price--on-sale .prod__price {
  color: #b22234;
}
.sf__scrolling-text .sf__scrolling-text-inner a em {
  font-size: 14px;
  letter-spacing: 2px;
  font-weight: 500;
}
.sf-header .sf-search-form {
  border: none;
}
.sf-header:not(.stuck) .sf-header__desktop .sf-logo {
  padding-top: 20px;
  padding-bottom: 20px;
}

.sf__index-custom-content a.sf__btn-link::after,
.sf__index-custom-content a.sf__btn-link::before,
.sf__index-custom-content-1 .image-card__inner .image-card__content a.sf__btn-link::after,
.sf__index-custom-content-1 .image-card__inner .image-card__content a.sf__btn-link::before {
  height: 1px;
}
.sf__index-custom-content-1 .image-card__inner .image-card__content .image__card-heading {
  line-height: 1.3;
}
.sf__index-hero .sf-hero__subtitle {
  text-transform: capitalize;
  font-size: 20px;
  line-height: 1.6;
  margin-bottom: 20px;
}
.sf__index-hero .sf-hero__title {
  line-height: 1.167;
  margin-bottom: 12px;
}
.sf__index-hero .sf-hero__text,
.sf__index-custom-content .rte p {
  font-size: 14px;
  line-height: 21px;
  text-transform: uppercase;
  letter-spacing: 0.1em;
  margin-bottom: 36px;
  color: var(--color-body-text);
}
.sf__newsletter-block-desc {
  margin-bottom: 16px;
  color: var(--color-body-text);
  text-transform: uppercase;
  letter-spacing: 0.1em;
  font-size: 14px;
  line-height: 1.5;
}
@media (min-width: 1280px) {
  .sf__index-custom-content h3 {
    font-size: 40px;
    line-height: 1.3;
    margin-bottom: 20px;
  }
}
@media (min-width: 1536px) {
  .sf__index-custom-content-1 .image-card__inner .image-card__content .image__card-heading {
    font-size: 40px;
    margin-bottom: 30px;
  }
}

@media (max-width: 767px) {
  .sf__index-custom-content-1 .image-card__content {
    padding-left: 0;
    padding-right: 0;
  }
  .sf__index-custom-content-1 .image-card__content .image-card__content-inner {
    width: 100%;
    text-align: left;
  }
  .sf__index-custom-content h3 br,
  .sf__index-custom-content-1 .image-card__content .image__card-heading br {
    display: none;
  }
  .sf__index-custom-content-1 .image-card__inner .image-card__content .image__card-heading {
    margin-bottom: 5px;
  }
  .sf__index-custom-content .sf-custom__block {
    width: 100%;
  }
  .sf__index-custom-content .rte p {
    margin-bottom: 0;
  }
  .sf__index-custom-content .sf-custom__text-cta {
    margin-top: 10px;
  }
  .sf__index-custom-content .sf-custom__block--text {
    margin-top: -30px;
  }
  .sf__index-slideshow .sf__mobile-button,
  .sf__index-hero .sf-hero__btn,
  .sf__index-hero .sf-hero__btn a.sf__btn {
    width: 100%;
  }
}
