current.custom_css:
.text-color-primary,
.sf__btn.sf__btn-white,
a.sf__btn.sf__btn-white {
  color: var(--color-primary);
}
.sf__pcard-name {
  font-weight: 600;
  font-size: 18px;
}
.sf__pcard-price .prod__price {
  font-weight: 700;
  font-size: 20px;
}
.sf__pcard-price.price--on-sale .prod__price {
  color: var(--color-primary);
}
.sf__index-custom-content .sf-custom__block-inner .rte {
  color: var(--text-color);
}
.sf__footer-block-custom_text .block-text {
  color: #000;
  font-size: 24px;
  line-height: 36px;
  font-weight: 600;
}
.sf__footer-block-menu .sf__footer-block-title {
  font-size: 24px;
  line-height: 32px;
}
.sf__index-img-with-text [class*="content__box-"] {
  position: relative;
  z-index: 1;
}

.sf__index-img-with-text [class*="content__box-"] .content__box {
  position: relative;
  z-index: 1;
}

.sf__index-press .rte p {
  font-size: 24px;
  line-height: 36px;
}
.sf__index-custom-content .sf-custom__block--text,
.sf__index-custom-content .sf-custom__block--video {
  background: #649670;
}

.sf__index-custom-content .sf-custom__block--video {
  align-items: center;
}

.sf__newsletter-section form.sf-newsletter .sf__newsletter-form-group {
  color: #000;
  background: #fff;
  border-radius: 12px;
}

.sf__newsletter-section form.sf-newsletter .sf__newsletter-form-group input,
.sf__newsletter-section form.sf-newsletter .sf__newsletter-form-group svg path {
  color: #000;
}

.sf__newsletter-section form.sf-newsletter input#ContactSubscribe-email::placeholder {
  color: #7e7e7e !important;
}

@media (min-width: 1025px) {
  .sf__index-custom-content .sf-custom__block--text .sf-custom__block-inner {
    margin-left: calc(100% - 513px);
    padding: 3rem;
  }
  .sf__index-custom-content .sf-custom__block--video {
    padding-right: 3rem;
  }

  .sf__index-img-with-text {
    padding: 50px 0;
  }

  .sf__index-img-with-text [class*="content__box-"]::after {
    position: absolute;
    content: "";
    width: 90%;
    height: calc(100% + 200px);
    top: -70px;
    left: 0;
    background-image: url(https://cdn.shopify.com/s/files/1/0042/0750/9567/files/bg_4b2c59fa-069b-480d-aa76-f3b9c4de7aa4.jpg?v=1622687661);
    background-repeat: no-repeat;
    background-size: contain;
  }
  .sf__index-img-with-text .section-my {
    margin-bottom: 200px;
  }
}
@media (min-width: 1230px) {
  .sf__index-custom-content .sf-custom__block--text .sf-custom__block-inner {
    margin-left: calc(100% - 615px);
    padding: 4rem;
  }
  .sf__index-custom-content .sf-custom__block--video {
    padding-right: 4rem;
  }
}
@media (min-width: 1366px) {
  .sf__index-custom-content .sf-custom__block--text .sf-custom__block-inner {
    margin-left: calc(100% - 683px);
    padding: 4rem;
  }
  .sf__index-custom-content .sf-custom__block--video {
    padding-right: 4rem;
  }
}
@media (min-width: 1536px) {
  .sf__index-custom-content .sf-custom__block--text .sf-custom__block-inner {
    margin-left: calc(100% - 768px);
    padding: 0 6rem;
  }
  .sf__index-custom-content .sf-custom__block--video {
    padding-right: 0;
  }
}
@media (max-width: 767px) {
  .sf__index-img-with-text [class*="content__box-"] .content__box {
    text-align: center;
    padding: 0 1rem;
  }
  .sf__index-img-with-text [class*="content__box-"] .content__subheading img {
    margin: 0 auto;
    max-width: 60px !important;
    margin-top: 30px;
  }
  .image__with-text  .section-my {
    margin-top: 5px;
  }
  .sf__index-featured-collection .section__subheading img {
    max-width: 80px !important;
  }
  .sf__index-custom-content .sf-custom__block--video {
    padding: 0;
  }
  .sf__index-custom-content #sf-custom__block--1dd7af76-ebfe-417a-a96d-a4f1ab210d8e {
    padding: 30px 24px;
  }
  .sf__index-custom-content #sf-custom__block--1dd7af76-ebfe-417a-a96d-a4f1ab210d8e img {
    max-width: 60px !important;
  }
}
