current.custom_css:
.sf__topbar-inner {
  font-size: 14px;
}
.section__heading {
  letter-spacing: 2px;
}
.sf__btn,
a.sf__btn {
  letter-spacing: 0.5px;
}
.sf__pcard .sf-pqv__button {
  text-transform: uppercase;
}
.sf__pcard-name,
.sf-nav li a.sf__parent-item,
.sf-footer .sf__footer-block-title,
.sf__index-testimonial .not-change p,
.sf__index-slideshow .sf__mobile-subheading,
.sf__index-slideshow .sf__slide-content .slide__block-subtitle {
  font-weight: 700;
}
.sf__index-slideshow .slide__block-desktop .slide__block-description {
  color: #fff;
  font-size: 15px;
}
.sf__index-slideshow .slide__block-desktop a.sf__btn {
  color: #fff;
}
.sf__index-custom-content-1 .image-card__content .sf__btn.sf__btn-link::before {
  background-color: rgba(0, 0, 0, 0.11);
}
.sf__index-custom-content-1 .image-card__content-inner {
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  height: 100%;
  justify-content: space-between;
}
.sf__index-hero .sf-hero__content .sf-hero__title {
  font-weight: 800;
  letter-spacing: 2px;
}
.sf__index-testimonial .not-change.text-sm p {
  font-weight: 500;
}
@media (min-width: 1024px) {
  .sf__index-custom-content-2 .sf-custom__newsletter {
    border-left: 1px solid #eee;
  }
  .sf__index-custom-content-2 .sf-custom__newsletter form {
    max-width: 450px;
    margin: 0 auto;
  }
  .sf-footer .sf__footer-bottom .container {
    border-top: 1px solid #d8d8d8;
  }
}
@media (min-width: 768px) {
  .sf-footer .sf__footer-block-custom_text .sf__footer-block-title {
    display: none;
  }
}
@media (min-width: 1536px) {
  .sf__index-slideshow .slide__block-desktop .slide__block-title {
    font-size: 130px;
    line-height: 135px;
    font-weight: 800;
  }
}
@media (max-width: 767px) {
  .sf__index-custom-content-3 h3.image__card-heading {
    font-size: 16px;
    margin-bottom: 0;
  }
  .sf__index-hero .sf-hero__content .sf-hero__subtitle {
    font-size: 14px;
    line-height: 21px;
  }
  .sf__index-hero .sf-hero__content .sf-hero__title {
    font-size: 36px;
    letter-spacing: 0;
  }
  .sf__index-custom-content-3 .image-card__content-inner a.sf__btn {
    display: none;
  }
  .sf__index-testimonial .section-my {
    background: #f5f5f5;
    margin: 0;
    padding-top: 40px;
    padding-bottom: 30px;
    padding-right: 0;
    padding-left: 0;
  }
  .sf__index-custom-content-2 .section-my {
    padding: 0;
  }
}

@media screen and (max-width: 767px) {
  .sf__index-hero .sf-hero__btn {
    width: 100%;
  }
  .sf__index-hero .sf-hero__btn a {
    background-color: #000000;
    width: 100%;
    border-color: #000000;
    color: #ffffff;
  }
  .sf__index-hero .sf-hero__btn a:hover {
    background-color: #555555;
    border-color: #555555;
    box-shadow: 0 12px 24px rgb(0 0 0 / 24%);
    transform: translate3d(0, -2px, 0);
  }
}

@media (max-width: 576px) {
  .sf__index-featured-collection .sf__featured-collection-button .sf__btn {
    width: 100%;
  }
}
