current.custom_css:
.sf__btn.sf__btn-primary,
a.sf__btn.sf__btn-primary {
  color: var(--color-heading-text);
}

a.sf__btn.sf__btn-link,
.sf__btn.sf__btn-link {
  color: var(--color-primary);
  border-color: var(--color-primary);
}

.sf__index-icon-box .sf-column {
  padding-bottom: 0;
}
body.template-index {
  background: #fffbf1;
}

.sf-instagram .section-my {
  margin-bottom: 0;
}

.font-body {
  font-family: "SF-Body-font";
}

.sf-featured-product .prod_prices .prod__price {
  font-weight: 600;
  color: var(--color-primary);
}
.sf-featured-product .prod__qty-lable {
  font-weight: 600;
}
.sf-featured-product .sf-prod__info .quantity-input {
  background: transparent;
  border: 2px solid;
  border-radius: 0;
  height: 50px;
}
.sf-featured-product .sf-prod__info .quantity-input input {
  background-color: transparent;
}
.sf-featured-product a.prod__title {
  font-family: "SF-Heading-font";
}

@media (min-width: 768px) {
  .image__with-text .section-my {
    margin-top: 60px;
    margin-bottom: 60px;
  }
  .section-py {
    padding-top: 100px;
    padding-bottom: 100px;
  }
  .sf__footer-form-newsletter {
    max-width: 80%;
  }
  .sf-hero__subtitle,
  .sf-hero__title {
    font-size: 5rem;
  }
  .sf-featured-product a.prod__title {
    font-size: 50px;
  }
}
.sf-featured-product .sf-pis__wrapper .justify-center {
  display: block;
}
.sf-featured-product .sf-pis__wrapper .justify-center {
  display: block;
}

.sf-featured-product .sf-image img {
  max-width: 410px;
  object-fit: contain;
}
.sf-featured-product .sf-prod__block {
  align-items: center;
}
@media (min-width: 768px) {
  .sf-featured-product .section-my {
    margin: 50px auto;
  }
}
@media (min-width: 1580px) {
  .sf__index-hero .sf-hero__title {
    font-size: 6.25rem;
    line-height: 1.1;
  }
  .sf__index-hero .sf-hero__text {
    font-size: 1.875rem;
    line-height: 1.4;
  }
  .sf__btn.sf__btn-primary {
    font-size: 1.125rem;
    line-height: 1;
  }
}
@media (min-width: 1024px) {
  .image__with-text .content__heading {
    font-size: 3.125;
    line-height: 1.28;
  }
  .sf-featured-product .sf-grid .sf-column:first-child {
    padding-right: 50px;
  }
  .sf-featured-product .sf-grid .sf-column:last-child {
    padding-left: 0;
  }
  .sf-featured-product .sf-grid .sf-column:first-child .sf-image--loaded img {
    left: auto;
  }
  .sf-testimonials .sf-testimonials__header .section__heading {
    font-size: 3.125rem;
    line-height: 1.42;
  }
}

.sf-featured-product .product-form__actions button.add-to-cart:hover {
  color: #fff;
}
.sf-testimonials .text-color-subtext + div.not-change p {
  font-weight: 600;
}
.sf-testimonials__container .sf-slider__controls-button:hover {
  border: none;
  box-shadow: none;
}
@media (min-width: 768px) and (max-width: 1024px) {
  .sf__index-hero .sf-hero__bg {
    background-position: 65% 50%;
  }
}
@media (min-width: 768px) {
  .sf__index-hero .sf-hero__text p {
    color: #ffffff;
  }
  .sf-featured-product .product-form__actions button.add-to-cart {
    color: var(--color-heading-text);
    font-size: 1.125rem;
    line-height: 1;
  }
}
@media (max-width: 767px) {
  .sf__index-hero .sf-hero__text p {
    color: var(--color-sub-text);
  }
  .sf__index-icon-box .sf-slider__controls .swiper-pagination {
    margin-top: 15px;
  }
  .sf-featured-product .product-form__actions .prod__option-label + div.flex {
    align-items: flex-end;
  }
  .sf-featured-product .product-form__actions .quantity-input__element {
    align-self: center;
  }
  .sf-featured-product .product-form__actions button.add-to-cart {
    height: 52px;
  }
  .sf-testimonials .section__header {
    margin-bottom: 0;
    margin-top: 20px;
  }
  .sf-testimonials .sf-slider__controls {
    margin-top: 0;
  }
