current.custom_css:
.font__weight-medium {
  font-weight: 500;
}
.font-body {
  font-family: Jost, sans-serif;
}
.font-size-34 {
  font-size: 34px;
  line-height: 44px;
}
.sf__pcard-image .sf__image-box {
  border-radius: 10px;
}
.sf__pcard-name {
  font-family: SF-Heading-font;
  font-size: 18px;
  font-weight: 400;
}
.sf__pcard-price {
  font-weight: 500;
}
.sf__pcard-price .prod__price {
  font-weight: 600;
}
.sf__pcard-price.price--on-sale .prod__price {
  color: #b22234;
}
.sf__newsletter-block-desc {
  margin-bottom: 30px;
  color: #696969;
}
.sf__footer-block-menu .sf__footer-block-title {
  font-size: 24px;
  line-height: 32px;
}
.sf-search__form,
.sf__footer-form-newsletter input.form-control {
  background-color: #f5f5f5;
}
.collection-card__image {
  border-radius: 10px;
}
.custom__html-block2 {
  padding: 70px 80px;
  background-image: url(https://cdn.shopify.com/s/files/1/0561/2742/2636/files/bg.jpg?v=1622194012);
  background-size: no-repeat;
  background-size: cover;
  background-position: center;
  border-radius: 8px;
}
.custom__html-block2 .block__content {
  text-align: center;
  display: inline-block;
}
.custom__html-block2 .block__content h3 {
  color: #fff;
  font-size: 48px;
  line-height: 56px;
  margin-bottom: 6px;
}
.custom__html-block2 .block__content p {
  color: rgba(255, 255, 255, 0.7);
}
.custom__html-block2 .block__social {
  margin-top: 50px;
  text-align: center;
}
.custom__html-block2 .block__social ul {
  margin: 0 -20px;
}
.custom__html-block2 .block__social ul li {
  display: inline-block;
  padding: 0 20px;
}
.custom__html-block2 .block__social ul li a {
  display: flex;
  align-items: center;
  color: #fff;
}
.custom__html-block2 .block__social ul li a img {
  height: 15px;
  margin-right: 10px;
}
.custom__html-block1 {
  padding: 0 70px;
  position: relative;
}
.custom__html-block1::after {
  position: absolute;
  content: "";
  top: 22px;
  left: 0;
  width: 100%;
  height: calc(100% - 56px);
  border-radius: 8px;
  background: #e3ebec;
  z-index: -1;
}
.custom__html-block1 img {
  max-width: 300px;
  margin-right: 70px;
}
.custom__html-block1 .block__content {
  position: relative;
  z-index: 1;
}
.custom__html-block1 .block__content-text {
  display: flex;
  align-items: center;
  justify-content: space-between;
  width: 100%;
}
.custom__html-block1 .block__content-text .block__heading h3 {
  font-size: 40px;
  line-height: 56px;
  margin-bottom: 4px;
}
.custom__html-block1 .block__content-text .block__heading p {
  color: #696969;
}
@media (min-width: 768px) {
  .section-my {
    margin-top: 100px !important;
    margin-bottom: 100px !important;
  }
  .section-py {
    padding-top: 100px !important;
    padding-bottom: 100px !important;
  }
  .sf__footer-form-newsletter {
    max-width: 80%;
  }
}
@media (max-width: 767px) {
  .custom__html-block1 {
    padding: 0 20px 20px;
  }
  .custom__html-block1 .block__content,
  .custom__html-block1 .block__content-text {
    flex-direction: column;
    text-align: center;
  }
  .custom__html-block1 img {
    margin-right: 0;
  }
  .custom__html-block1 .block__content-text .block__heading h3 {
    font-size: 24px;
    line-height: 30px;
    margin-top: 20px;
  }
  .custom__html-block1 .block__content-text .block__heading p {
    margin-bottom: 20px;
  }
  .custom__html-block1::after {
    height: 100%;
  }
  .custom__html-block2 {
    padding: 50px 20px;
    background-position: left;
  }
  .custom__html-block2 .block__content h3 {
    font-size: 24px;
    line-height: 30px;
  }
  .custom__html-block2 .block__social ul li {
    margin-bottom: 15px;
  }
}

.sf__index-custom-content-1 .sf__btn::before {
  height: 1px;
}
.sf__index-custom-content-1 .sf__btn::after {
  height: 1px;
}

.sf-header__desktop .sf-menu__banner .sf__btn::before {
  height: 1px;
}
.sf-header__desktop .sf-menu__banner .sf__btn::after {
  height: 1px;
}

.sf-header__desktop .sf-search-form button {
  order: 1;
  padding-right: 0;
}
.sf-header__desktop .sf-search-form span {
  order: 2;
}
.sf-header__desktop .sf-search-form {
  min-width: 487px;
  background-color: #f5f5f5;
}
@media (max-width: 1560px) {
  .sf-header__desktop .sf-search-form {
    min-width: 287px;
  }
}
@media(max-width: 767px) {
    .sf-footer .sf__accordion-button.sf__footer-block-title {
        font-size: calc(var(--font-base-size, 16)*1px);
    }
}
