current.custom_css:
/* Custom css */
.top_bar-label {
  font-size: 15px;
  line-height: 1.2;
  font-weight: 700;
  padding: 3px 15px;
  border-radius: 20px;
  background: #E94D38;
  margin-right: 10px;
  color: #fff;
}
.sf-cart-count, .sf-wishlist-count,
.sf-nav li.sf-menu-item-parent a.sf__parent-item {
  font-weight: 700;
}
.sf__btn.sf__btn-white, a.sf__btn.sf__btn-white,
.sf__btn.sf__btn-link, a.sf__btn.sf__btn-link {
  color: var(--color-primary);
}
.sf__btn.sf__btn-link:before, a.sf__btn.sf__btn-link:before,
.sf__btn.sf__btn-link:after, a.sf__btn.sf__btn-link:after {
  height: 1px;
}
.section__header {
  margin-bottom: 40px;
}
.home-banner__slideshow[id$="1621243260e1af0c20"] .sf__slide-content .slide__block-title {
  line-height: 1.14;
  margin-bottom: 17px;
}
.home-banner__slideshow[id$="1621243260e1af0c20"] .sf__slide-content .slide__block-description {
  line-height: 2;
  font-size: 18px;
  margin-bottom: 28px;
}
.collection-list[id$="16225316461d1cff80"] .collection-card--inside .collection-card__info {
  top: 0;
  display: flex;
  flex-direction: column;
  justify-content: center;
}
.collection-list[id$="16225316461d1cff80"] .collection-card--inside .collection-card__info h3 a {
  font-size: 22px;
  font-weight: 700;
  line-height: 1.14;
  margin-bottom: 7px;
}
.collection-list[id$="16225316461d1cff80"] .collection-card--inside .collection-card__image {
  border-radius: 5px;
}
.collection-list[id$="16225316461d1cff80"] .collection-card--inside .collection-card__info .sf__btn-icon {
  display: none;
}
.sf__pcard .sf__pcard-name,
.sf__pcard .sf__pcard-price.price--on-sale .prod__price {
  font-weight: 700;
}
.sf__pcard .sf__pcard-price.price--on-sale .prod__price {
  color: #E94D38;
}
.image__with-text .content__heading {
  line-height: 1.28;
  font-size: 50px;
  margin-bottom: 25px;
}
.image__with-text .prose {
  margin-bottom: 25px;
}
.sf-icon-box[id$="16306855824867f977"] .sf-icon-box__text h3 {
  line-height: 1.14;
  color: #000;
  font-size: 22px;
  margin-bottom: 16px;
} 
.sf__footer-block-title {
  font-weight: 700 !important;
  font-size: 18px;
}
.sf-footer form.sf__footer-form-newsletter.form__style-minimal .sf__newletter__form-input {
  border-color: #3D6642;
}
.sf-footer form.sf__footer-form-newsletter.form__style-minimal .sf__newletter__form-input::placeholder {
  color: var(--color-primary);
}
.sf-footer form.sf__footer-form-newsletter.form__style-minimal svg path {
  fill: #3D6642;
}


@media(min-width: 1024px) {
  .sf__footer-block-newsletter {
  	padding-right: 50px;
  }
  .sf__footer-block-newsletter .sf__footer-block-title {
    font-size: 36px;
    line-height: 1.5;
    margin-bottom: 12px;
    color: var(--color-primary);
      }
  .sf__footer-block-newsletter .sf__newsletter-block-desc {
    padding: 0;
    margin-bottom: 30px;
  }
}

@media(min-width: 1280px) {
  .collection-list[id$="16225316461d1cff80"] .collection-list__inner--overflow {
    padding-right: calc((100vw - 1280px)/ 2 + 5px);
    padding-left: calc((100vw - 1280px) / 2 + 5px);
  }
}

@media(min-width: 1536px) {
  .sf__footer-block-newsletter {
  	padding-right: 195px;
  }
}

@media(min-width: 1680px) {
  .home-banner__slideshow[id$="1621243260e1af0c20"] .sf__slide-content .slide__block-title {
    font-size: 70px;
  }
}

@media(max-width: 767px) {
  .home-banner__slideshow[id$="1621243260e1af0c20"] .sf__slide-content .sf__mobile-button {
    width: 100%;
    color: var(--color-btn-text);
    background: var(--color-btn-bg);
  }
  .image__with-text .content__heading {
    font-size: 34px;
  }
  .collection-list[id$="16225316461d1cff80"] .collection-card--inside .collection-card__info {
    padding: 12px;
    position: absolute;
  }
  .collection-list[id$="16225316461d1cff80"] .collection-card--inside .collection-card__info h3 a {
    font-size: 16px !important;
    margin-bottom: 0;
  }
}
