current.custom_css:
.section__heading,
.sf-hero__title {
  line-height: 1.2;
}
.section__subheading,
.sf-hero__subtitle {
  text-transform: uppercase;
  letter-spacing: 0.05em;
}
.sf__pcard-name {
  font-family: "SF-Heading-font";
  font-size: 24px;
  font-weight: normal !important;
}
.sf__pcard-price.price--on-sale .prod__price {
  color: #bb5210;
  font-size: 20px;
}
.sf__btn {
  padding-top: 12px;
  padding-bottom: 8px;
}
.sf-header .sf-header__desktop .sf-menu-logo-bar {
  padding-left: 35px;
  padding-right: 35px;
}
.sf__index-hero .sf-hero {
  background-color: #f5f1e9;
}
.sf__index-hero .sf-hero__bg {
  background-size: contain;
  background-position: right;
  top: 150px;
  bottom: 0;
  height: auto;
}
.sf__index-hero .sf-hero__content .sf-hero__subtitle {
  margin-bottom: 60px;
}
.sf__index-hero .sf-hero__title {
  line-height: 1.1875;
  margin-bottom: 18px;
}
.sf__index-hero .sf-hero__text {
  text-transform: uppercase;
  letter-spacing: 0.05em;
  margin-bottom: 45px;
  color: var(--color-body-text);
}

.sf__index-custom-html .rte .section__heading {
  margin-bottom: 13px;
}
.sf__index-custom-html .block__content-header {
  margin-bottom: 40px;
}
.sf__index-custom-html .block__content-body {
  margin-bottom: 60px;
}
.sf__index-custom-html .block__content-body h3 {
  font-size: 24px;
  line-height: 1.33;
  margin-bottom: 10px;
}
.sf__index-custom-html .block__content-header p,
.sf__index-custom-html .block__content-body p {
  font-size: 15px;
}
.sf__index-custom-html .block__content-footer {
  padding-top: 24px;
  margin-top: 90px;
  border-top: 1px solid #ededed;
}
.sf__index-custom-html .block__content-footer .block__item h4 {
  font-size: 48px;
  line-height: 1.33;
}
.sf__index-custom-html .block__content-footer .block__item p {
  font-size: 18px;
}
.sf__index-hero-1 .sf-hero__content .sf-hero__subtitle {
  margin-bottom: 30px;
}
.sf__index-hero-1 .sf-hero__content .sf-hero__title {
  margin-bottom: 10px;
}
.sf__index-hero-1 .sf-hero__content .sf-hero__text {
  margin-bottom: 50px;
}
.sf__index-icon-box .section-py {
  padding-top: 50px;
}
.sf__index-icon-box .sf-icon-box__text {
  margin-top: 28px;
}
.sf__index-icon-box .sf-icon-box__text h3 {
  line-height: 1.33;
  font-size: 24px;
  margin-bottom: 8px;
}
.sf__index-icon-box .sf-icon-box__text .rte {
  font-size: 15px;
}
.sf__index-press {
  background-image: url("https://cdn.shopify.com/s/files/1/0595/7291/2305/files/bg-2_756d0ce8-7c87-4a6f-9044-0d2eafdcd3b8.jpg?v=1632109894");
  background-position: top;
  background-size: cover;
  background-repeat: no-repeat;
}
.sf__index-press .press-content {
  background-image: url("https://cdn.shopify.com/s/files/1/0595/7291/2305/files/bg-3.svg?v=1632110218");
  background-repeat: no-repeat;
  background-position: 50% 10%;
  padding-top: 100px;
  padding-bottom: 50px;
}
.sf__index-press .press-inner {
  padding-top: 100px;
}
.sf__index-press .press-content .press-content__item .rte {
  line-height: 1.4;
  font-size: 34px;
  font-family: "SF-Heading-font";
  max-width: 100%;
}
.sf__index-press .press-logo .sf-image {
  max-width: var(--max-width, 100%);
}

.sf__index-hero-2 .sf-hero__bg {
  top: 70px;
  left: 50px;
  right: 50px;
  width: calc(100% - 100px);
}
.sf__index-hero-2 .sf-hero__subtitle {
  background-image: url("https://cdn.shopify.com/s/files/1/0595/7291/2305/files/icon-4.svg?v=1632112223");
  padding: 150px 0 50px;
  background-repeat: no-repeat;
  background-position: center;
  margin-bottom: 0;
}
.sf-footer {
  background-image: url("https://cdn.shopify.com/s/files/1/0595/7291/2305/files/bg-footer_1.png?v=1632112778");
  padding-top: 8%;
  background-size: cover;
  background-repeat: no-repeat;
}
.sf-footer .sf__footer-block-newsletter {
  margin: 0 auto;
  text-align: center;
}
.sf-footer .sf__footer-block-title {
  font-family: "SF-Body-font";
  text-transform: uppercase;
  letter-spacing: 0.05em;
}
.sf-footer .sf__newsletter-block-desc {
  padding: 0;
  margin-bottom: 36px;
  font-family: "SF-Heading-font";
  font-size: 40px;
  line-height: 1.3;
}
.sf-footer .sf__footer-block-newsletter form {
  margin: 0 auto;
  max-width: 80%;
}
.sf-footer form.sf__footer-form-newsletter .sf__newletter__form-input {
  border: 2px solid;
  border-radius: 52px;
}
.sf-footer
  form.sf__footer-form-newsletter
  .sf__newletter__form-input::placeholder {
  color: #fff;
}
.sf-footer .sf__footer-block-newsletter .social-media-links {
  justify-content: center;
  margin-top: 50px;
}
.sf__prd-custom-content .section-py {
  padding-bottom: 0;
}
@media (min-width: 1536px) {
  .section__heading,
  .sf-hero__title {
    font-size: 56px;
  }
}

@media (min-width: 1680px) {
  .sf__index-hero .sf-hero__title {
    font-size: 64px;
  }
}

@media (max-width: 767px) {
  .sf__index-hero .sf-hero__content {
    text-align: center;
  }
  .sf__index-hero .sf-hero__content .sf-hero__subtitle {
    margin-bottom: 20px;
  }
  .sf__index-hero .sf-hero__content .sf-hero__subtitle img {
    max-width: 80px;
    margin: 0 auto;
  }
  .sf__index-hero .sf-hero__inner {
    padding-top: 0;
    padding-bottom: 40px;
  }
  .sf__index-hero .sf-hero__title {
    font-size: 36px;
  }
  .sf__index-hero .sf-hero__text {
    margin-bottom: 30px;
  }
  .sf__index-hero .sf-hero__btn,
  .sf__index-hero .sf-hero__btn .sf__btn {
    width: 100%;
  }
  .sf-header header.sf-header__mobile .sf-logo a {
    padding-bottom: 20px;
  }
  .sf__index-custom-html .block__content-body {
    margin-bottom: 30px;
  }
  .sf__index-custom-html .block__content-footer {
    margin-top: 30px;
  }
  .sf__index-custom-html .block__content-footer .block__item h4 {
    font-size: 32px;
  }
  .sf__index-hero-1 .sf-hero__content .sf-hero__title {
    font-size: 36px;
  }
  .sf__index-hero-1 .sf-hero__inner {
    padding-top: 40px;
    padding-bottom: 0;
  }
  .sf__index-hero-1 .sf-hero__content .sf-hero__text {
    margin-bottom: 30px;
    color: var(--color-sub-text);
  }
  .section__subheading,
  .sf-hero__subtitle {
    font-size: 14px;
    margin-bottom: 8px;
  }
  .sf__pcard-name {
    font-size: 18px;
  }
  .sf__index-press {
    margin-top: -100px;
    z-index: 0;
  }
  .sf__index-press .press-content .press-content__item .rte {
    font-size: 24px;
  }
  .sf__index-press .press-content {
    background-size: 40%;
    padding-top: 0;
    padding-bottom: 0;
  }
  .sf__index-hero-2 .sf-hero__inner {
    padding-top: 40px;
    padding-bottom: 40px;
  }
  .sf__index-hero-2 .sf-hero__subtitle {
    background-size: 30%;
    padding: 50px 0 10px;
  }
  .sf__index-hero-2 .sf-hero__bg {
    top: 0;
    left: 0;
    right: auto;
    width: 100%;
  }
  .sf-footer .sf__newsletter-block-desc {
    font-size: 20px;
    margin-bottom: 20px;
  }
  .sf-footer {
    padding-top: 20%;
    margin-top: -30px;
  }
}
.sf__custom-5-section-collapse .sf__tab-item-label {
  font-size: 1.5rem;
  font-weight: normal;
  line-height: 1.5;
  border-bottom-color: #cccccc;
  font-family: 'SF-Heading-font';
  padding: 0;
  margin: 1rem 0;
}
.sf__custom-icon .section-py {
  padding-bottom: 0;
  padding-top: 0;
}
.sf__custom-text .section-py {
  padding-top: 20px;
  padding-bottom: 0;
}
.sf__custom-text .section-py .section__header {
  margin-bottom: 0;
}
.sf__custom-images .section-py {
  padding-top: 0;
}
.sf__custom-5-section-collapse .sf__tab-item-content .text-color-subtext {
    font-size: 15px;
    line-height: 1.6;
    margin-bottom: 1.25rem;
}
.sf__custom-5-section-collapse .sf__tabs-custom .sf__tab-item {
    margin-bottom: 0;
}
.sf__custom-5-section-collapse .sf__tab-item-label:before {
    right: 5px;
}
